var path = require('path');
var Stipe = require('./index');

var stipe = new Stipe({
  debug: true,
  match: 'db/bmail/',
  client: {port: 9999},
  filter: function(file) {
    //console.log('filter file:'+file);
    var name = path.basename(file);

    if(name[0] === '.') {
      return false;
    }

    return true;
  },
  attach: function(dir) {
    //console.log('attach:'+dir);
    var name = path.basename(dir);

    if(name[0] === '.') {
      return false;
    }

    return true;
  },
  trigger: function(event, callback) {
    console.log('SEND:'+JSON.stringify(event));
    callback(null, JSON.stringify(event.file));
  }
})
